import { Component } from '@angular/core';

@Component({
  selector: 'app-calculator',
  templateUrl: './calculator.component.html',
  styleUrls: ['./calculator.component.css']
})
export class CalculatorComponent {
  towrite = '0';
  currentvalue = '';
  historyList: string[] = [];

  writetoinput(value: string) {
    this.currentvalue = this.currentvalue + value;
    this.towrite = this.currentvalue;
  }

  // equal() {
  //   const result = eval(this.currentvalue);
  //   this.towrite = result.toString();
  //   this.currentvalue = this.towrite;


  //   this.historyList.push(`${this.currentvalue} = ${result}`);
  // }
  equal() {
    const result = eval(this.currentvalue);
    this.towrite = result.toString();
    this.currentvalue = this.towrite;

    this.historyList.push(`${this.currentvalue} = ${result}`);
    this.showHistory(); // Call showHistory to update the displayed history
  }


  clear() {
    this.currentvalue = '';
    this.towrite = '0';
  }

  back() {
    this.currentvalue = this.currentvalue.slice(0, -1);
    this.towrite = this.currentvalue;
    if (this.towrite === '') {
      this.towrite = '0';
    }
  }

  showHistory() {


    console.log(this.historyList);


  }
}
<div class="container" style="display: flex;flex-direction: row;">
  <div
    style="
      display: flex;
      justify-content: left;
      margin-left: 10%;
      align-items: center;
      height: 100vh;
    "
  >
    <div class="mycalc">
      <input [(ngModel)]="towrite" type="text" />

      <div class="all-btn">
        <div class="all-btn-num">
          <button (click)="writetoinput('1')">1</button>
          <button (click)="writetoinput('2')">2</button>
          <button (click)="writetoinput('3')">3</button>
          <button (click)="writetoinput('4')">4</button>
          <button (click)="writetoinput('5')">5</button>
          <button (click)="writetoinput('6')">6</button>
          <button (click)="writetoinput('7')">7</button>
          <button (click)="writetoinput('8')">8</button>
          <button (click)="writetoinput('9')">9</button>
          <button (click)="writetoinput('0')">0</button>
          <button (click)="writetoinput('.')">.</button>
          <button (click)="back()">back</button>
        </div>
        <div class="all-btn-sign">
          <button (click)="writetoinput('-')">-</button>
          <button (click)="writetoinput('+')">+</button>
          <button (click)="writetoinput('*')">*</button>
          <button (click)="writetoinput('/')">/</button>
        </div>
      </div>
      <div class="btn-clr">
        <button (click)="showHistory()">history</button>
        <button (click)="clear()">C</button>
        <button (click)="equal()">=</button>
      </div>
    </div>
  </div>
    <div
      style="

        justify-content: left;
        margin: 10% 10%;
        align-items: top;
        height: 100vh;
      "
    >
      <h3>history</h3>
      <p *ngFor="let historyItem of historyList">{{ historyList }}</p>
    </div>

</div>
